    public void desenhaCabeca(){
        for(int i = 0; i < 30; i++){
            for(int j = 0; j < 60; j++){
                if(mapa[i][j] == 0){
                     System.out.printf("\u001B[40m ");
                }
                else if(mapa[i][j] == 1){
                     System.out.printf("\u001B[41m ");
                }
                else if(mapa[i][j] == 2){
                     System.out.printf("\u001B[46m ");
                }
                else if(mapa[i][j] == 3){
                     System.out.printf("\u001B[42m ");
                }
                else if(mapa[i][j] == 4){
                     System.out.printf("\u001B[44m ");
                }
                else if(mapa[i][j] == 5){
                     System.out.printf("\u001B[43m ");
                }
                else if(mapa[i][j] == 6){
                     System.out.printf("\u001B[45m ");
                }
            }
            System.out.println("");
        }
    }
    
    public void instanciaLeucocitos(){
        for(int i = 0; i < 10; i++){
            Calendar c = Calendar.getInstance();
            xRan = ran.nextInt(60);
            yRan = ran.nextInt(30);
            leucocitos.add(new Leucocitos(c.get(Calendar.SECOND), 6, xRan, yRan));
            mapa[leucocitos.get(i).getY()][leucocitos.get(i).getX()] = leucocitos.get(i).getCor();
        }
    }
    
    public void alocaInfluenzas(Influenza inf){
        influenzas.add(inf);
        mapa[inf.getY()][inf.getX()] = inf.getCor();
    }
    
    public void mataLeucocitos(){
        for(int i = 0; i < leucocitos.size();i++){
            int xRan = ran.nextInt(60);
            int yRan = ran.nextInt(30);
            Calendar c = Calendar.getInstance();
            nasc = leucocitos.get(i).getNascimento();
            if(c.get(Calendar.SECOND) - leucocitos.get(i).getNascimento() >= 7){
                if(leucocitos.size() <= 10){
                    leucocitos.remove(i);
                    leucocitos.add(new Leucocitos(c.get(Calendar.SECOND),6,xRan,yRan));
                }
                else{
                    leucocitos.remove(i);
                    leucocitos.trimToSize();
                }
            }
        }
    }
    
    public void moveLeucocitos(){
        for(int i = 0; i < leucocitos.size(); i++){
            leucocitos.get(i).mover();
            mapa[leucocitos.get(i).getY()][leucocitos.get(i).getX()] = leucocitos.get(i).getCor();
        }
    }
    
    public void moveInfluenzas(){
        for(int i = 0; i < influenzas.size(); i++){
            influenzas.get(i).mover();
            mapa[influenzas.get(i).getY()][influenzas.get(i).getX()] = influenzas.get(i).getCor();
        }
    }
    
    public void verificaCelula(){
        Calendar c = Calendar.getInstance();
        for(int i = 0; i < influenzas.size(); i++){
           if(influenzas.get(i).estaNaCelula(mapa) == true){
                Influenza inf = new Influenza(5,ran.nextInt(60),ran.nextInt(30));
                alocaInfluenzas(inf);
            }
           if(influenzas.get(i).colidiuLeucocito(mapa) == true){
                influenzas.remove(i);
                influenzas.trimToSize();
                l = new Leucocitos(c.get(Calendar.SECOND),6,ran.nextInt(60),ran.nextInt(30));
                leucocitos.add(l);
            }
        }
    }
    
